printf "\n\nalias getip=getipfunc\n\n" >> ~/.bashrc; printf "getipfunc() {\nifconfig \$1 | awk \'/inet addr/{print substr(\$2,6)}\' | tr -d \"\\\n\"\n}\n" >> ~/.bashrc;printf "\n\nalias msfconsole=msffunc\n\n" >> ~/.bashrc;printf "msffunc() {\n if [ \"\$#\" -eq 1 ]\n then\n  \$(which msfconsole) -x \"setg lhost \`getip \$1\`\"\n else\n  \$(which msfconsole) \$@\n fi\n}\n" >>~/.bashrc;source ~/.bashrc;echo "[+] laziness protocol implemented"
